package com.bytefinger.toutuo.biz.auditdocument.service.impl;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import com.bytefinger.common.das.domain.vo.QueryPage;import com.bytefinger.common.security.annotation.DataFill;import com.bytefinger.toutuo.biz.auditdocument.domain.AuditDocument;import com.bytefinger.toutuo.biz.auditdocument.mapper.AuditDocumentMapper;import com.bytefinger.toutuo.biz.auditdocument.service.IAuditDocumentService;import lombok.AllArgsConstructor;import org.springframework.stereotype.Service;import org.springframework.web.bind.annotation.RequestBody;/** * <p> * 项目文件 服务实现类 * </p> * * @author patrick * @since 2023-01-31 */@Service@AllArgsConstructorpublic class AuditDocumentServiceImpl extends ServiceImpl<AuditDocumentMapper, AuditDocument> implements IAuditDocumentService {    private final AuditDocumentMapper projectDocumentMapper;    @Override    public void addDocument(AuditDocument projectDocument) {        getBaseMapper().insert(projectDocument);    }    @Override    public Page<AuditDocument> page(QueryPage queryPage) {        return projectDocumentMapper.list(queryPage.toPage(), queryPage.getWrapper(),queryPage.getDbParams());    }    @Override    @DataFill    public Page<AuditDocument> pageTwo(@RequestBody QueryPage queryPage) {        return projectDocumentMapper.pageTwo(queryPage.toPage(), queryPage.getWrapperByPrefix("bc."), queryPage.getDbParams());    }}