package com.bytefinger.toutuo.biz.auditstep.mapper;import com.bytefinger.toutuo.biz.auditstep.domain.AuditStepMenu;import com.bytefinger.toutuo.biz.auditstep.domain.AuditStepMenuConfig;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.*;import java.util.List;/** * <p> * 项目步骤菜单配置表 Mapper 接口 * </p> * * @author Jone * @since 2023-01-31 */@Mapperpublic interface AuditStepMenuConfigMapper extends BaseMapper<AuditStepMenuConfig> {    @Select("SELECT s.id,s.name,s.level,c.sorts from biz_audit_step_menu s LEFT JOIN biz_audit_step_menu_config c on s.id = c.step_menu_id" +            "where c.audit_type = #{auditType} and s.parent_id = #{parentId}")    List<AuditStepMenu> listByParentId(@Param("auditType") String auditType, @Param("parentId") Long parentId);    @Delete("delete from biz_audit_step_menu_config where audit_type = #{auditType}")    void deleteByAuditType(@Param("auditType") String auditType);    @Insert({            "<script>",            "insert into biz_audit_step_menu_config(audit_type, step_menu_id) values",            "<foreach item='item' index='index' collection='list' separator=','>",            "(#{item.auditType},#{item.stepMenuId})",            "</foreach>",            "</script>"    })    int batchSave(@Param("list")List<AuditStepMenuConfig> auditStepMenuConfigs);}