package com.bytefinger.toutuo.biz.auditstep.mapper;import com.bytefinger.toutuo.biz.auditstep.domain.AuditStepMenu;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 项目步骤菜单 Mapper 接口 * </p> * * @author Jone * @since 2023-01-31 */@Mapperpublic interface AuditStepMenuMapper extends BaseMapper<AuditStepMenu> {    @Select({            "<script>",            "SELECT m.id,m.parent_id,m.name,m.code,m.level,m.is_document,m.oa_approval,m.offline_approval,m.show,m.sorts",            "from biz_audit_step_menu m where m.parent_id = 0 and m.id in(",            "SELECT c.step_menu_id from biz_audit_step_menu_config c where c.audit_type = #{auditType})",            "</script>"    })    List<AuditStepMenu> listbyAuditTypeOne(@Param("auditType") String auditType);    @Select({            "<script>",            "SELECT m.id,m.parent_id,m.name,m.code,m.level,m.is_document,m.oa_approval,m.offline_approval,m.show,m.sorts",            "from biz_audit_step_menu m where m.id in(",            "SELECT c.step_menu_id from biz_audit_step_menu_config c where c.audit_type = #{auditType}) order by m.sorts asc",            "</script>"    })    List<AuditStepMenu> listbyAuditType(@Param("auditType") String auditType);}