package com.bytefinger.toutuo.biz.auditteam.service.impl;import com.baomidou.mybatisplus.core.toolkit.Wrappers;import com.bytefinger.common.security.annotation.DataFill;import com.bytefinger.toutuo.biz.auditteam.domain.AuditTeam;import com.bytefinger.toutuo.biz.auditteam.mapper.AuditTeamMapper;import com.bytefinger.toutuo.biz.auditteam.service.IAuditTeamService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import com.bytefinger.toutuo.biz.project.domain.ProjectTeam;import lombok.AllArgsConstructor;import org.springframework.stereotype.Service;import java.util.List;/** * <p> * 审计团队信息 服务实现类 * </p> * * @author Jone * @since 2025-08-05 */@Service@AllArgsConstructorpublic class AuditTeamServiceImpl extends ServiceImpl<AuditTeamMapper, AuditTeam> implements IAuditTeamService {    private final AuditTeamMapper auditTeamMapper;    @DataFill    @Override    public List<AuditTeam> list(Long auditId) {        return auditTeamMapper.selectList(Wrappers.<AuditTeam>lambdaQuery().eq(AuditTeam::getAuditId, auditId)                .orderByDesc(AuditTeam::getCreateTime));    }}