package com.bytefinger.toutuo.biz.businessopportunity.mapper;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.bytefinger.toutuo.biz.businessopportunity.domain.BusinessOpportunity;import com.bytefinger.toutuo.biz.businessopportunity.domain.BusinessOpportunityArea;import com.bytefinger.toutuo.biz.businessopportunity.domain.BusinessOpportunityUnlock;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 商机 Mapper 接口 * </p> * * @author chengwei * @since 2023-12-17 */@Mapperpublic interface BusinessOpportunityUnlockMapper extends BaseMapper<BusinessOpportunityUnlock> {    @Select("select   \n" +            "sum(case when DATE(unlock_date) = CURDATE()\n" +            " then  unlock_bussiness_Count else 0 end ) unlock_bussiness_Count,\n" +            "sum(unlock_stock_Count)  as unlockStockCount , sum(unlock_company_Count)  as unlock_company_Count  from biz_business_opportunity_unlock")    public BusinessOpportunityUnlock getUnlockCountSum();    @Select("select  *  from biz_business_opportunity_unlock where   DATE(unlock_date) = CURDATE();")    public List<BusinessOpportunityUnlock> getunlockCountToday();    @Select("select * from biz_business_opportunity_area")    public List<BusinessOpportunityArea> getQlmAreaList();    @Select("select * from biz_business_opportunity_area where parent_id=0")    public List<BusinessOpportunityArea> getQlmAreaProvinceList();    @Select("select * from biz_business_opportunity_area where parent_id in(select id from biz_business_opportunity_area where parent_id=0 )")    public List<BusinessOpportunityArea> getQlmAreaCityList();    @Select("SELECT * from biz_business_opportunity_area where parent_id = #{parentId}")    public List<BusinessOpportunityArea> getQlmAreaListByParentId(@Param("parentId") String parentId);    @Select({            "<script>",            "SELECT *  from biz_business_opportunity_area where parent_id   in",            "<foreach item='item' index='index' collection='parentIds' open='(' separator=',' close=')'>",            "#{item}",            "</foreach>",            "</script>"    })    public List<BusinessOpportunityArea> getQlmAreaListByParentIds(@Param("parentIds") List<String> parentIds);}