package com.bytefinger.toutuo.biz.companyfinance.mapper;import com.bytefinger.toutuo.biz.companyfinance.domain.ProjectCompanyFinance;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * <p> * 财务信息 Mapper 接口 * </p> * * @author Jone * @since 2023-03-17 */@Mapperpublic interface ProjectCompanyFinanceMapper extends BaseMapper<ProjectCompanyFinance> {    @Select({    "<script>",        "SELECT  cf.total_assets as total_assets,  cf.net_assets  as net_assets, ",        " cf.total_profit as total_profit, cf.net_profit  as net_profit from biz_project_company_finance cf,",        "(select max(id) as id from biz_project_company_finance where company_id=#{companyId,jdbcType=BIGINT}  GROUP BY begin_time,end_time) pcf",        "where cf.id = pcf.id ",        "ORDER BY cf.create_time desc ",        "LIMIT 1",    "</script>"    })    ProjectCompanyFinance totalByCompanyId(@Param("companyId")Long companyId);}