package com.bytefinger.toutuo.biz.companyhigh.service.impl;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.bytefinger.common.core.service.ThisService;import com.bytefinger.common.das.domain.vo.QueryPage;import com.bytefinger.common.security.annotation.DataFill;import com.bytefinger.toutuo.biz.companyfinance.domain.ProjectCompanyPayment;import com.bytefinger.toutuo.biz.companyhigh.domain.ProjectCompanyHighLevelMeeting;import com.bytefinger.toutuo.biz.companyhigh.domain.ProjectCompanyHighLevelMeetingApproval;import com.bytefinger.toutuo.biz.companyhigh.mapper.ProjectCompanyHighLevelMeetingMapper;import com.bytefinger.toutuo.biz.companyhigh.service.IProjectCompanyHighLevelMeetingService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import com.bytefinger.toutuo.biz.project.service.impl.ProjectServiceImpl;import com.bytefinger.toutuo.common.service.BizService;import lombok.AllArgsConstructor;import org.springframework.stereotype.Service;import org.springframework.util.CollectionUtils;/** * <p> * 三会记录 服务实现类 * </p> * * @author Jone * @since 2023-03-19 */@Service@AllArgsConstructorpublic class ProjectCompanyHighLevelMeetingServiceImpl extends BizService<ProjectCompanyHighLevelMeetingMapper, ProjectCompanyHighLevelMeeting> implements IProjectCompanyHighLevelMeetingService, ThisService<ProjectCompanyHighLevelMeetingServiceImpl> {    private ProjectCompanyHighLevelMeetingMapper projectCompanyHighLevelMeetingMapper;    @Override    @DataFill    public Page<ProjectCompanyHighLevelMeeting> page(QueryPage queryPage) {        return projectCompanyHighLevelMeetingMapper.list(queryPage.toPage(), queryPage.getWrapper(), queryPage.getDbParams());    }    @Override    @DataFill    public Page<ProjectCompanyHighLevelMeeting> page(Long stepMenuId, QueryPage queryPage) {        Page<ProjectCompanyHighLevelMeeting> page = projectCompanyHighLevelMeetingMapper.list(queryPage.toPage(), queryPage.getWrapper(), queryPage.getDbParams());        if(CollectionUtils.isEmpty(page.getRecords())){            return page;        }        super.dataFillDcoument(stepMenuId, page);        return page;    }    @Override    @DataFill    public ProjectCompanyHighLevelMeeting getById(Long id) {        return projectCompanyHighLevelMeetingMapper.getById(id);    }}