package com.bytefinger.toutuo.biz.companyrisk.mapper;import com.baomidou.mybatisplus.core.conditions.Wrapper;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.core.toolkit.Constants;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.bytefinger.toutuo.biz.companyoperate.domain.ProjectCompanyOperateReport;import com.bytefinger.toutuo.biz.companyrisk.domain.ProjectCompanyRiskInspection;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;import java.util.Map;/** * <p> * 子公司-风险检查 Mapper 接口 * </p> * * @author Jone * @since 2023-03-17 */@Mapperpublic interface ProjectCompanyRiskInspectionMapper extends BaseMapper<ProjectCompanyRiskInspection> {    @Select("SELECT id,company_id,node,milepost_time,reminder_time,content,create_time,create_user_id,remark FROM biz_project_company_risk_inspection " +            "WHERE 1 = 1  AND  TO_DAYS(reminder_time) = TO_DAYS(now())")    List<ProjectCompanyRiskInspection> listByReminderTime();    Page<ProjectCompanyRiskInspection> list(IPage<ProjectCompanyRiskInspection> page, @Param(Constants.WRAPPER) Wrapper<ProjectCompanyRiskInspection> wrapper,                                           @Param(Constants.COLUMN_MAP) Map<String, Object> params);    ProjectCompanyRiskInspection getById(@Param(value = "id") Long id);    void insertriskInspection(ProjectCompanyRiskInspection riskInspection);}