package com.bytefinger.toutuo.biz.companyrisk.service.impl;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import com.bytefinger.common.das.domain.vo.QueryPage;import com.bytefinger.common.security.annotation.DataFill;import com.bytefinger.toutuo.biz.companyoperate.domain.ProjectCompanyOperateReport;import com.bytefinger.toutuo.biz.companyrisk.domain.ProjectCompanyRiskInspection;import com.bytefinger.toutuo.biz.companyrisk.domain.ProjectCompanyRiskInspectionApproval;import com.bytefinger.toutuo.biz.companyrisk.mapper.ProjectCompanyRiskInspectionApprovalMapper;import com.bytefinger.toutuo.biz.companyrisk.service.IProjectCompanyRiskInspectionApprovalService;import com.bytefinger.toutuo.common.service.BizService;import lombok.AllArgsConstructor;import org.springframework.stereotype.Service;import org.springframework.util.CollectionUtils;/** * <p> * 风险处理 服务实现类 * </p> * * @author Jone * @since 2023-03-19 */@Service@AllArgsConstructorpublic class ProjectCompanyRiskInspectionApprovalServiceImpl extends BizService<ProjectCompanyRiskInspectionApprovalMapper, ProjectCompanyRiskInspectionApproval> implements IProjectCompanyRiskInspectionApprovalService {    private final ProjectCompanyRiskInspectionApprovalMapper projectCompanyRiskInspectionApprovalMapper;    @Override    @DataFill    public Page<ProjectCompanyRiskInspectionApproval> page(Long stepMenuId, QueryPage queryPage) {        Page<ProjectCompanyRiskInspectionApproval> page = projectCompanyRiskInspectionApprovalMapper.list(queryPage.toPage(), queryPage.getWrapper(), queryPage.getDbParams());        if(CollectionUtils.isEmpty(page.getRecords())){            return page;        }        super.dataFillDcoument(stepMenuId, page);        return page;    }    @Override    @DataFill    public Page<ProjectCompanyRiskInspectionApproval> page(QueryPage queryPage) {        return projectCompanyRiskInspectionApprovalMapper.list(queryPage.toPage(), queryPage.getWrapper(), queryPage.getDbParams());    }    @Override    @DataFill    public ProjectCompanyRiskInspectionApproval getById(Long id) {        return projectCompanyRiskInspectionApprovalMapper.getById(id);    }}