package com.bytefinger.toutuo.biz.external.mapper;import com.baomidou.mybatisplus.core.conditions.Wrapper;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.bytefinger.toutuo.api.biz.user.domain.IntMainDept;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 主数据组织表 Mapper 接口 * </p> * * @author Jone * @since 2023-02-13 */@Mapperpublic interface IntMainDeptMapper extends BaseMapper<IntMainDept> {    @Select({        "<script>",        "select *,",        "case when (SELECT count(1) from sys_dept_main_dept s where s.main_dept_id = d.DESC1) >0 then 1",        "else 0 end",        "as relation ",        "from int_main_dept d",        "</script>"    })    List<IntMainDept> list();}