package com.bytefinger.toutuo.biz.performance.mapper;import com.bytefinger.toutuo.api.system.core.domain.SysDictData;import com.bytefinger.toutuo.biz.performance.domain.BO.PerformanceList;import com.bytefinger.toutuo.biz.performance.domain.vo.CurrMothTotalVo;import com.bytefinger.toutuo.biz.performance.domain.vo.CurrYearTotalVO;import com.bytefinger.toutuo.biz.performance.domain.vo.HeadStatisticalVO;import com.bytefinger.toutuo.biz.project.domain.Project;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import java.math.BigDecimal;import java.util.List;import java.util.Map;/** * <p> * 预算收入年数据表 Mapper 接口 * </p> * * @author patrick * @since 2022-11-24 */@Mapperpublic interface PerformanceMapper {    /**     * 获取指点年数据     *     * @param ids     * @param currYear     * @return     */    List<PerformanceList> groupInData(@Param("ids") List<Long> ids, @Param("currYear") Integer currYear);    /**     * 获取指点年数据     *     * @param ids     * @param currYear     * @return     */    List<PerformanceList> groupOutData(@Param("ids") List<Long> ids, @Param("currYear") Integer currYear);    /**     * 锁定 解锁     *     * @param tableName     * @param recordId     * @param locked     */    void locked(@Param("tableName") String tableName, @Param("recordId") Long recordId, @Param("currYear") Integer currYear, @Param("locked") Integer locked);    /**     * 年度目标支出预算设置:锁定 解锁     *     * @param tableName     * @param recordId     * @param locked     */    void lockedIndicator(@Param("tableName") String tableName, @Param("recordId") Long recordId, @Param("currYear") Integer currYear, @Param("locked") Integer locked);    /**     * 获取预算统计数据     *     * @param ids     * @param currYear     * @return     */    PerformanceList statisticsInData(@Param("ids") List<Long> ids, @Param("level") Integer level, @Param("currYear") Integer currYear,@Param("currMonth") Integer currMonth,@Param("fieldKey") String fieldKey);    CurrMothTotalVo currMothTotal(@Param("userId") Long userId);    CurrYearTotalVO currYearTotal(@Param("userId") Long userId,@Param("currYear") Integer currYear);    HeadStatisticalVO StatisticalLeadDays(@Param("userId") Long userId);    HeadStatisticalVO StatisticalLead(@Param("userId") Long userId);    HeadStatisticalVO StatisticalOpportunityDays(@Param("userId") Long userId);    HeadStatisticalVO StatisticalOpportunity(@Param("userId") Long userId);    HeadStatisticalVO StatisticalAgreementDays(@Param("userId") Long userId);    HeadStatisticalVO StatisticalAgreement(@Param("userId") Long userId ,@Param("currYear") Integer currYear);    HeadStatisticalVO StatisticalAgreementParticipation(@Param("userId") Long userId ,@Param("currYear") Integer currYear);    Integer getLeadCount(Long userid);    Integer getOpportunityCount(Long userid);    List<Integer> getAgreementCount(Long userid);    Integer getCustomerCount(Long userid);    Integer getProjectCount(Long userid);    List<Project> getProjectIdByUser(@Param("currYear") Integer currYear,@Param("userId") Long userId);    List<Project> getProjectIdByUserParticipation(@Param("currYear") Integer currYear,@Param("userId") Long userId);    Map<String, BigDecimal> getSJBBSRTotalByProjectId(@Param("recordId") Long recordId);    SysDictData  getDictData(@Param("dictValue") String dictValue);}