package com.bytefinger.toutuo.biz.project.mapper;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.bytefinger.toutuo.biz.project.domain.ProjectRiskInspection;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 项目-风险检查 Mapper 接口 * </p> * * @author Jone * @since 2023-02-01 */@Mapperpublic interface ProjectRiskInspectionMapper extends BaseMapper<ProjectRiskInspection> {    @Select("SELECT id,project_id,node,milepost_time,reminder_time,introduction,create_time,create_user_id,remark FROM biz_project_risk_inspection WHERE 1 = 1  AND  TO_DAYS(reminder_time) = TO_DAYS(now())")    List<ProjectRiskInspection> listByReminderTime();}