package com.bytefinger.toutuo.biz.project.service.impl;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.core.toolkit.Wrappers;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import com.bytefinger.common.das.domain.vo.QueryPage;import com.bytefinger.common.security.annotation.DataFill;import com.bytefinger.toutuo.biz.project.domain.ProjectTeam;import com.bytefinger.toutuo.biz.project.mapper.ProjectTeamMapper;import com.bytefinger.toutuo.biz.project.service.IProjectTeamService;import lombok.AllArgsConstructor;import org.springframework.stereotype.Service;import java.util.List;/** * <p> * 项目团队信息 服务实现类 * </p> * * @author Jone * @since 2023-01-31 */@Service@AllArgsConstructorpublic class ProjectTeamServiceImpl extends ServiceImpl<ProjectTeamMapper, ProjectTeam> implements IProjectTeamService {    private final ProjectTeamMapper projectTeamMapper;    @DataFill    @Override    public IPage<ProjectTeam> page(QueryPage queryPage) {        return projectTeamMapper.selectPage(queryPage.toPage(), queryPage.getWrapper());    }    @DataFill    @Override    public List<ProjectTeam> list(Long projectId) {        return projectTeamMapper.selectList(Wrappers.<ProjectTeam>lambdaQuery().eq(ProjectTeam::getRecordId, projectId)                .orderByDesc(ProjectTeam::getCreateTime));    }}