package com.bytefinger.toutuo.biz.projectcompany.service.impl;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import com.bytefinger.common.das.domain.vo.QueryPage;import com.bytefinger.common.security.annotation.DataFill;import com.bytefinger.toutuo.biz.projectcompany.domain.ProjectCompanyExecutives;import com.bytefinger.toutuo.biz.projectcompany.domain.ProjectCompanyShareholderLog;import com.bytefinger.toutuo.biz.projectcompany.mapper.ProjectCompanyShareholderLogMapper;import com.bytefinger.toutuo.biz.projectcompany.service.IProjectCompanyShareholderLogService;import com.bytefinger.toutuo.common.service.BizService;import org.springframework.stereotype.Service;import org.springframework.util.CollectionUtils;/** * <p> * 股权信息变更记录 服务实现类 * </p> * * @author Jone * @since 2023-03-17 */@Servicepublic class ProjectCompanyShareholderLogServiceImpl extends BizService<ProjectCompanyShareholderLogMapper, ProjectCompanyShareholderLog> implements IProjectCompanyShareholderLogService {    @Override    @DataFill    public IPage<ProjectCompanyShareholderLog> page(QueryPage queryPage) {        return this.page(queryPage.toPage(), queryPage.getWrapper());    }    @Override    @DataFill    public IPage<ProjectCompanyShareholderLog> page(Long stepMenuId, QueryPage queryPage) {        Page<ProjectCompanyShareholderLog> page = this.page(queryPage.toPage(), queryPage.getWrapper());        if(CollectionUtils.isEmpty(page.getRecords())){            return page;        }        super.dataFillDcoument(stepMenuId, page);        return page;    }}