package com.bytefinger.toutuo.biz.projectextension.entity;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.annotation.TableName;import com.bytefinger.common.core.domain.BaseEntity;import com.bytefinger.common.security.annotation.Excel;import com.bytefinger.toutuo.common.annotation.HistoryFieldLog;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;import lombok.experimental.Accessors;/** * <p> * 项目步骤完成状态 * </p> * * @author Jone * @since 2023-01-31 */@Data@Accessors(chain = true)@TableName("biz_project_step_expansion")@ApiModel(value = "ProjectStep对象", description = "项目步骤完成状态")public class ProjectStepExpansion extends BaseEntity {    /**     * 项目id     */    @ApiModelProperty(value = "项目id", hidden = false, required = false)    @TableField("project_id")    @HistoryFieldLog("项目id")    @Excel(name = "项目id")    private Long projectId;    /**     * 步骤菜单id     */    @ApiModelProperty(value = "步骤菜单id", hidden = false, required = false)    @TableField("step_menu_id")    @HistoryFieldLog("步骤菜单id")    @Excel(name = "步骤菜单id")    private Long stepMenuId;    /**     * 状态 0-未完成 1-已完成     */    @ApiModelProperty(value = "状态 0-未完成 1-已完成", hidden = false, required = false)    @TableField("status")    @HistoryFieldLog("状态 0-未完成 1-已完成")    @Excel(name = "状态 0-未完成 1-已完成")    private Integer status;    /***     * 审批状态     * 0  未发起审批     * 1  审批中     * 2  审批通过     * 3 审批驳回     * 8 线下审批通过     * 9无需审批     **/    @ApiModelProperty(value = "审批状态", hidden = false, required = false)    @TableField("approval_status")    @HistoryFieldLog("审批状态")    private Integer approvalStatus;}