package com.bytefinger.toutuo.biz.projectextension.mapper;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.bytefinger.toutuo.biz.projectextension.entity.ProjectStepExpansionMenu;import com.bytefinger.toutuo.biz.projectstep.domain.ProjectStepMenu;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 拓后项目步骤菜单 * </p> * * @author ycj * @since 2023-03-23 */@Mapperpublic interface ProjectStepExpansionMenuMapper extends BaseMapper<ProjectStepExpansionMenu> {    @Select({            "<script>",            "SELECT m.id,m.parent_id,m.name,m.code,m.level,m.is_document,m.oa_approval,m.offline_approval,m.show,m.",            "from biz_project_step_menu m where m.parent_id = 0 and m.id in(",            "SELECT c.step_menu_id from biz_project_step_menu_config c where c.project_type = #{projectType})",            "</script>"    })    List<ProjectStepMenu> listbyProjectTypeOne(@Param("projectType") String projectType);    @Select({            "<script>",            "SELECT m.id,m.parent_id,m.name,m.code,m.level,m.is_document,m.oa_approval,m.offline_approval,m.show",            "from biz_project_step_expansion_menu m order by m.sorts asc",            "</script>"    })    List<ProjectStepExpansionMenu> listbyProjectType();}