package com.bytefinger.toutuo.biz.projectstep.mapper;import com.bytefinger.toutuo.biz.projectstep.domain.ProjectStepMenu;import com.bytefinger.toutuo.biz.projectstep.domain.ProjectStepMenuConfig;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.bytefinger.toutuo.biz.user.domain.SysPostMenu;import org.apache.ibatis.annotations.*;import java.util.List;/** * <p> * 项目步骤菜单配置表 Mapper 接口 * </p> * * @author Jone * @since 2023-01-31 */@Mapperpublic interface ProjectStepMenuConfigMapper extends BaseMapper<ProjectStepMenuConfig> {    @Select("SELECT s.id,s.name,s.level,c.sorts from biz_project_step_menu s LEFT JOIN biz_project_step_menu_config c on s.id = c.step_menu_id" +            "where c.project_type = #{projectType} and s.parent_id = #{parentId}")    List<ProjectStepMenu> listByParentId(@Param("projectType") String projectType, @Param("parentId") Long parentId);    @Delete("delete from biz_project_step_menu_config where project_type = #{projectType}")    void deleteByProjectType(@Param("projectType") String projectType);    @Insert({            "<script>",            "insert into biz_project_step_menu_config(project_type, step_menu_id) values",            "<foreach item='item' index='index' collection='list' separator=','>",            "(#{item.projectType},#{item.stepMenuId})",            "</foreach>",            "</script>"    })    int batchSave(@Param("list")List<ProjectStepMenuConfig> projectStepMenuConfigs);}