package com.bytefinger.toutuo.biz.user.mapper;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.bytefinger.toutuo.api.biz.user.domain.IntMainBm;import com.bytefinger.toutuo.biz.user.domain.DeptMainDept;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 部门和主数据部门关联表 Mapper 接口 * </p> * * @author Jone * @since 2023-02-13 */@Mapperpublic interface DeptMainDeptMapper extends BaseMapper<DeptMainDept> {    @Select({"select * from int_main_bm d where d.CODE in(select md.main_dept_id from sys_dept_main_dept md where md.dept_id=#{deptId})"})    List<IntMainBm> listByDeptId(@Param("deptId") Long deptId);}