mapper.sql:
SELECT
    DISTINCT report.*,company.name as companyName, company.company_biz_no as companyBizNo, company.company_no as companyNo
FROM
biz_project_company_appraise_report report,
biz_project_company company ,
biz_project project
where company.id = report.company_id and company.project_id=project.id and project.service_status='ZAI_GUAN' and project.expire= 'YOU_XIAO'

    and company.name  like  '%${cm.companyName}%'





        and (
        project.company_id IN

            #{item}

        OR project.id IN ( SELECT bpu.record_id FROM biz_project_team bpu WHERE bpu.user_id = #{cm.userId})
        OR project.attributor_user_id = #{cm.userId}
        OR project.create_user_id = #{cm.userId}
        OR company.principal_id=#{cm.userId}
        )









        AND (
        project.attributor_user_id = #{cm.userId}
        OR project.id IN ( SELECT bpu.record_id FROM biz_project_team bpu WHERE bpu.user_id = #{cm.userId})
        OR project.create_user_id = #{cm.userId}
        OR company.principal_id=#{cm.userId}
        )
