mapper.sql:
 SELECT
DISTINCT approval.*,company.name as companyName, company.company_biz_no as companyBizNo, company.company_no as
companyNo
FROM
biz_project_company_high_level_meeting_approval approval,
biz_project_company company ,
biz_project project
where company.id = approval.company_id and company.project_id=project.id and project.service_status='ZAI_GUAN' and project.expire= 'YOU_XIAO'

    and approval.id in(select document.record_id from biz_project_company_document document where
    document.document_template_id = ${cm.documentTemplateId})


    and company.name like '%${cm.companyName}%'





        and (
        project.company_id IN

            #{item}

        OR project.id IN ( SELECT bpu.record_id FROM biz_project_team bpu WHERE bpu.user_id = #{cm.userId})
        OR project.attributor_user_id = #{cm.userId}
        OR project.create_user_id = #{cm.userId}
        OR company.principal_id=#{cm.userId}
        )









        AND (
        project.attributor_user_id = #{cm.userId}
        OR project.id IN ( SELECT bpu.record_id FROM biz_project_team bpu WHERE bpu.user_id = #{cm.userId})
        OR project.create_user_id = #{cm.userId}
        OR company.principal_id=#{cm.userId}
        )
