mapper.select:
select pro.id AS projectId,pro.project_name AS projectName,dept.dept_id AS deptId,dept.dept_name AS deptName,IFNULL(pro.contract_amount / 10000, 0) AS contractAmount,IFNULL(pro.contract_annual_amount / 10000, 0) AS contractAnnualAmount
,pro.expect_service_years 'year' from biz_project_step ps
JOIN biz_project pro ON ps.project_id = pro.id
JOIN sys_dept dept ON dept.dept_id = pro.company_id AND dept.del_flag = 0
WHERE ps.`status` = 1 AND ps.step_menu_id = 18
AND ps.handle_time >= #{startTime} AND ps.handle_time <= #{endTime}

    AND pro.company_id in

        #{companyId}







        and (
        pro.company_id IN

            #{item}

        OR pro.id IN ( SELECT bpu.record_id FROM biz_project_team bpu WHERE bpu.user_id = #{cm.userId})
        OR pro.attributor_user_id = #{cm.userId}
        OR pro.create_user_id = #{cm.userId}
        )









        AND (
        pro.attributor_user_id = #{cm.userId}
        OR pro.id IN ( SELECT bpu.record_id FROM biz_project_team bpu WHERE bpu.user_id = #{cm.userId})
        OR pro.create_user_id = #{cm.userId}
        )
