mapper.select:
select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.`query`, m.visible, m.status,
ifnull(m.perms,'') as perms, m.is_frame, m.is_cache, m.menu_type, m.icon, m.order_num, m.create_time
from sys_menu m
left join sys_post_menu pm on pm.menu_id = m.menu_id
left join sys_user_dept_post sudp on sudp.post_id = pm.post_id
left join sys_user u on u.user_id = sudp.user_id
left join sys_dept d on d.dept_id = sudp.dept_id
left join sys_post p on p.post_id = sudp.post_id
where u.user_id = #{params.userId}
and sudp.dept_id = #{params.deptId}
and sudp.post_id = #{params.postId}

    AND m.menu_name like concat('%', #{menuName}, '%')


    AND m.visible = #{visible}


    AND m.status = #{status}

order by m.parent_id, m.order_num




        AND menu_name like concat('%', #{menuName}, '%')


        AND visible = #{visible}


        AND status = #{status}


order by parent_id, order_num

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, `query` = #{query}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}

mapper.select:
select count(1)
from sys_menu
where parent_id = #{menuId}

select m.menu_id from sys_menu m left join sys_post_menu rm on m.menu_id = rm.menu_id where rm.post_id = #{postId}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_post_menu rm on m.menu_id = rm.menu_id and rm.post_id = #{postId})
order by m.parent_id, m.order_num
